
public class Simpleifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
	int 	num=70;
		if(num>=40&&num<50)
		{
			System.out.println("num is grater than 45 and num is less than 50");
		}
		else if(num>=50&&num<60)
		{
			System.out.println("num is grater than or equal to 50 and less than 60");
		}
		else if(num>=70&&num<80)
		{
			System.out.println("num is graten or equal to 70 and num is less than 80");
		}
		else
		{
			System.out.println("num is less than 45");
		}

	}

}
